public class If_ElseDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hi");
		int a = 100;
		int b = 20;
		if(a<b)
		{
			System.out.println("a is smaller than b");
		}
		else
		{
			System.out.println("Greater");
		}
		System.out.println("Bye");

	}

}
